/**
 * Detect Electron renderer process, which is node, but we should
 * treat as a browser.
 */

if (typeof process !== 'undefined' && process.type === 'renderer') {
  module.exports = require('body-parser/node_modules/debug/src/browser');
} else {
  module.exports = require('body-parser/node_modules/debug/src/node');
}
